mapper.select:
SELECT member_id "id", name, email, TO_CHAR(regdate, 'YYYY-MM-DD HH24:MI:SS') regdate, original_profile, stored_profile
FROM member
WHERE member_id = #{value}

mapper.update:
UPDATE member
SET MEMBER_EMAIL = #{email}, MEMBER_PASSWD = #{passwd}
WHERE MEMBER_ID = #{id}

mapper.select:
SELECT MEMBER_ID "id", MEMBER_NAME "name",MEMBER_PHOTO "profileImage" ,MEMBER_STOREDPHOTO "storedProfile",MEMBER_EMAIL "email",CLUB_NUM "clubNum",MEMBER_PASSWD "passwd", MEMBER_DIR "dir",MEMBER_NUM "memberNum"
FROM member
WHERE member_id = #{id} AND MEMBER_PASSWD = #{passwd}

SELECT member_id "id", name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate FROM member
and member_id = #{value}
and name LIKE '%' || #{value} || '%'

SELECT member_id "id", name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate
