mapper.select:
SELECT member_id "id", name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate
FROM member
WHERE member_id = #{value}

SELECT member_id "id", name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate
FROM member
WHERE member_id = #{id} AND password = #{passwd}

mapper.update:
UPDATE member
SET password = #{passwd}, email = #{email}
WHERE member_id = #{id}

mapper.select:
SELECT member_id "id", name, email, TO_CHAR(regdate, 'yyyy-MM-DD HH24:MI:SS') regdate FROM member
and member_id = #{value}
and name LIKE '%' || #{value} || '%'

